mapper.insert:
INSERT INTO attachments(attachments_no, product_no, review_no, post_no, comment_no, attachment_name, reg_date) SELECT seq_attachments_no.NEXTVAL, A.* FROM(
SELECT #{attachments.productNo:INTEGER} as product_no, #{attachments.reviewNo:INTEGER} as review_no, #{attachments.postNo:INTEGER} as post_no, #{attachments.commentNo:INTEGER} as comment_no, #{attachments.attachmentName} as attachment_name, SYSDATE as reg_date from dual
) A

mapper.select:
SELECT * FROM attachments

	
		
			product_no = #{productNo}
		
		
			review_no = #{reviewNo}
		
		
			post_no = #{postNo}
		
		
			comment_no = #{commentNo}
